mapper.select:
select 
from into t_prop_index t
where t.prop_id=#{propId}


  		select 

  		from t_prop_index t, t_professional p
  		where t.obj_id = p.special_id

  			and t.prop_type = #{propType}


		   and t.status = #{status}


  			and p.special_type = #{specialType}


  			and p.status = #{specialStatus}


			and p.starttime  <=  #{checkTime}
			and p.endtime > #{checkTime}

  		order by t.create_time desc
		limit 1

select
from t_prop_index t where 1=1
and t.prop_type = #{propType}
and t.status = #{status}
order by t.create_time desc limit 1


  		select  
  		from t_prop_index t
  		where 1=1

  			and t.resume like '%${resume}%'


  			and t.prop_type=#{propType}


		   and t.status=#{status}


  		select  
  		from t_prop_index t
  		where 1=1

  			and t.resume like '%${resume}%'


  			and t.prop_type=#{propType}


		   and t.status=#{status}
