mapper.select:
	select 

	from t_shopping_cart as sc  INNER JOIN t_product p ON sc.product_id=p.product_id and p.status=1
	where 1 = 1



   		and sc.customer_id = #{customer_id,jdbcType=INTEGER}




   		and sc.product_id = #{product_id,jdbcType=INTEGER}




   		and sc.spec_id = #{spec_id,jdbcType=INTEGER}




   		and sc.cart_id = #{cart_id,jdbcType=INTEGER}


  	LIMIT 1

SELECT ,c.nickname,p.product_code,p.product_name, ps.spec_name,ps.spec_img,ps.spec_price FROM t_shopping_cart sc INNER JOIN t_customer c ON sc.customer_id=c.customer_id INNER JOIN t_product p ON sc.product_id=p.product_id and p.status=1 INNER JOIN t_spec ps ON sc.spec_id = ps.spec_id AND sc.product_id = ps.product_id AND ps.status = 1 WHERE sc.customer_id = #{customerId}
and sc.cart_id in (${cartIds})
and sc.product_id=#{productId}
and sc.spec_id=#{specId}
ORDER BY sc.create_time DESC

SELECT SUM(countNum) from (
(SELECT COUNT(1) countNum from t_coupon_info where status = 0 AND NOW()  >  starttime AND NOW()  <  endtime

	AND customer_id = #{customerId}

) UNION (SELECT COUNT(1) countNum from t_single_coupon_spec ss LEFT JOIN t_single_coupon sc ON ss.coupon_id = sc.coupon_id
WHERE sc.`status` = 1 AND NOW()  >  sc.start_time AND NOW()  <  sc.end_time

	AND ss.product_id = #{productId}


	AND ss.spec_id = #{specId}

))temp
